package com.zhangzhuo;

import java.util.Scanner;

public class Tixin {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double A;
		double B;
		double H;
		Scanner in = new Scanner(System.in);
		A = in.nextDouble();
		B = in.nextDouble ();
		H = in.nextDouble() ;
		System.out.println((A + B) * H / 2);
	}

}
