package com.zhangzhuo;

import java.util.Scanner;

public class Tixin {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int A;
		int B;
		int H;
		Scanner in = new Scanner(System.in);
		A = in.nextInt();
		B = in.nextInt();
		H = in.nextInt();
		System.out.println((A + B) * H / 2);
	}

}
